import React from 'react';
import { useNavigate } from 'react-router-dom';

function Dashboard() {
    const navigate = useNavigate(); // Use useNavigate to handle redirection

    const handleLogout = () => {
        // Clear any stored authentication information (e.g., tokens)
        localStorage.removeItem('token');  // if you're using tokens
        sessionStorage.removeItem('token'); // or sessionStorage, depending on where it's stored
        
        // Redirect the user to the login page
        navigate('/');
    };

    return (
        <div>
            <h1>Welcome to the Dashboard!</h1>
            {/* Dashboard content here */}
            <button onClick={handleLogout}>Logout</button>
        </div>
    );
}

export default Dashboard;
